import React from "react";
import { Fade } from "react-reveal";
import Clymanew from '../images/Clymanew.png'
import Particles from 'react-particles-js';
import PlacesAutocomplete from 'react-places-autocomplete';
import {geocodeByAddress,geocodeByPlaceId,getLatLng} from 'react-places-autocomplete';


class LandingPage extends React.Component {

	state={
		address: ''
	}



  render(){
    return <div>
      <Fade left duration={2000} distance="40px">
				<div className='intro'>
					<h1 style={{fontFamily: 'Audiowide', fontSize: '35pt'}}>Introducing</h1>
				</div>
        <div className='lp-logo'>
          <img style={{width: '650px'}} src={Clymanew} alt='logo'></img>
        </div>
      </Fade>
			<div className='slogan'>
			<Fade left duration={2000}>
				<h2>Access real-time air quality readings and forecasts from anywhere in the world.</h2>
			</Fade>
			</div>

			<Fade right duration={1000}>
			<div className='landing-search'>
			<PlacesAutocomplete
        value={this.state.address}
        onChange={this.handleChange}
        onSelect={this.handleSelect}
      >
        {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
          <div>
            <input
              {...getInputProps({
                placeholder: 'Search Places ...',
                className: 'location-search-input',
              })}
            />
            <div className="autocomplete-dropdown-container">
              {loading && <div>Loading...</div>}
              {suggestions.map(suggestion => {
                const className = suggestion.active
                  ? 'suggestion-item--active'
                  : 'suggestion-item';
                // inline style for demonstration purpose
                const style = suggestion.active
                  ? { backgroundColor: '#fafafa', cursor: 'pointer' }
                  : { backgroundColor: '#ffffff', cursor: 'pointer' };
                return (
                  <div
                    {...getSuggestionItemProps(suggestion, {
                      className,
                      style,
                    })}
                  >
                    <span>{suggestion.description}</span>
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </PlacesAutocomplete>
			</div>
			</Fade>


      <div class='part-cntr'>
      <Particles
      style={{width: '100%', height: '100%'}}
    canvasClassName={'particles'}
    params={{
	    "particles": {
	        "number": {
	            "value": 60,
	            "density": {
	                "enable": true,
	                "value_area": 1500
	            }
	        },
	        "line_linked": {
	            "enable": true,
	            "opacity": 0.1
	        },
	        "move": {
	            "direction": "right",
	            "speed": 0.10
	        },
	        "size": {
	            "value": 1
	        },
	        "opacity": {
	            "anim": {
	                "enable": true,
	                "speed": 1,
	                "opacity_min": 0.05
	            }
	        }
	    },
	    "interactivity": {
	        "events": {
            "onhover": {
              "enable": true,
              "mode": "connect"
          },
	            "onclick": {
	                "enable": true,
	                "mode": "push"
	            }
	        },
	        "modes": {
	            "push": {
	                "particles_nb": 1
              },
              "connect": {
                'opacity': 5,
            }
          }
	    },
	    "retina_detect": true
	}} />
    </div>
    <img src='https://www.tmrow.com/static/5c33e6e4f32b13a7a70c2a4612f31647/097fa/climatestage-background1.jpg' alt='bg'></img>
    </div>
  }
}

// const WrappedContainer = GoogleApiWrapper({
//   apiKey: "AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8",
// })(LandingPage);

// export default (WrappedContainer);

export default LandingPage
